const sliders=document.getElementsByClassName("my_input");for(let t of sliders){const i=t.min,n=t.max,s=t.value;t.style.background=`linear-gradient(to right, #FE4A01FF 0%, #FE4A01FF ${(s-i)/(n-i)*100}%, #DEE2E6 ${(s-i)/(n-i)*100}%, #DEE2E6 100%)`,t.oninput=function(){this.style.background=`linear-gradient(to right, #FE4A01FF 0%, #FE4A01FF ${(this.value-this.min)/(this.max-this.min)*100}%, #DEE2E6 ${(this.value-this.min)/(this.max-this.min)*100}%, #DEE2E6 100%)`}}